tasks.task:
if [ ! -d "${workingDirectory}/Inputs" ]; then
	# wget http://static.sing-group.org/software/dreimt/database/sources/${dbZipName}
	unzip ${workingDirectory}/${dbZipName} -d ${workingDirectory}
	if [ ! -z ${removeDatabaseZip} ]; then 
		rm ${dbZipName}
	fi
fi

echo "Signatures with PubMedID = NA:"
awk -F'\t' '{ if($14 == "NA") { print $0 } }' ${workingDirectory}/${intermediateDirectory}/${intermediateSignaturesFile}
echo -e "\nSignatures with empty PubMedID:"
awk -F'\t' '{ if($14 == "") { print $0 } }' ${workingDirectory}/${intermediateDirectory}/${intermediateSignaturesFile}

dbSigNamesTmpFile=$(mktemp /tmp/DB_signature_names.XXXXXX) cat ${workingDirectory}/${signaturesFile} | awk -F'\t' '{gsub("_UP$|_sig$", "", $1); if($12 != "") {print $1}}' | sort -u > ${dbSigNamesTmpFile}
gmtFilesSigNamesTmpFile=$(mktemp /tmp/GMTs_signature_names.XXXXXX) cat ${workingDirectory}/${gmtSignaturesFile}| awk '{gsub("_UP$|_DN$|_sig$", "", $1); print $1}' > ${gmtFilesSigNamesTmpFile} cat ${workingDirectory}/${gmtGeneSetsFile} | awk '{gsub("_UP$|_DN$|_sig$", "", $1); print $1}' >> ${gmtFilesSigNamesTmpFile}
sortedGmtFilesSigNamesTmpFile=$(mktemp /tmp/GMTs_signature_names_sorted.XXXXXX) cat ${gmtFilesSigNamesTmpFile} | sort -u > ${sortedGmtFilesSigNamesTmpFile}
diffFile=$(mktemp /tmp/DB_vs_GMT_signature_names_DIFF.XXXXXX) diff ${dbSigNamesTmpFile} ${sortedGmtFilesSigNamesTmpFile} > ${diffFile}
count=$(cat ${diffFile} | grep '>' | wc -l) exitCode=0
if [[ $count -ne 0 ]] ; then echo -e "There are $count signatures that appear in the GMT files but not in the ${signaturesFile}" cat ${diffFile} | grep '>' exitCode=1 fi exit $exitCode
rm $dbSigNamesTmpFile rm $gmtFilesSigNamesTmpFile rm $sortedGmtFilesSigNamesTmpFile rm $diffFile


setwd(Sys.getenv("workingDirectory"));
library(GSEABase, quietly = TRUE);
immune.signature = getGmt(Sys.getenv("gmtSignaturesFile"));
status <- 0;

for(i in seq(1,length(immune.signature),2)) {
	up <- geneIds(immune.signature[i]);
	down <- geneIds(immune.signature[i+1]);
	intersection <- intersect(up[[1]], down[[1]]);
	if(length(intersection) > 0) {
		cat(i, "\t", names(immune.signature[i]), "\t", length(intersection),"\n");
		status <- 1;
	}
};
quit(status = status);

rm -rf ${workingDirectory}/${sqlDirectory}
mkdir ${workingDirectory}/${sqlDirectory}
rm -f ${workingDirectory}/${dbVersionsFile}
if [ ! -z ${dbVersionsDirectory} ]; then 
	cp ${dbVersionsDirectory}/${dbVersionsFile} ${workingDirectory}/${dbVersionsFile}
else
	wget http://static.sing-group.org/software/dreimt/database/sources/database-versions.txt -O ${workingDirectory}/${dbVersionsFile}
fi

currentVersion=$(cat ${workingDirectory}/${dbVersionsFile} | grep -P '\tT$' | cut -f 1) rm -rf ${workingDirectory}/${sqlDirectory}/${currentVersion} mkdir -p ${workingDirectory}/${sqlDirectory}/${currentVersion}
cat ${workingDirectory}/${sqlDirectory}/fill_genes_universe.sql ${workingDirectory}/${sqlDirectory}/fill_drug.sql ${workingDirectory}/${sqlDirectory}/fill_article_metadata.sql ${workingDirectory}/${sqlDirectory}/fill_signatures.sql ${workingDirectory}/${sqlDirectory}/fill_signatures_updown_interactions.sql ${workingDirectory}/${sqlDirectory}/fill_signatures_geneset_interactions.sql ${workingDirectory}/${sqlDirectory}/fill_signatures_updown_genes.sql ${workingDirectory}/${sqlDirectory}/fill_signatures_geneset_genes.sql ${workingDirectory}/${sqlDirectory}/fill_database_versions.sql ${workingDirectory}/${sqlDirectory}/fill_precalculated_examples.sql ${workingDirectory}/${sqlDirectory}/fill_dreimt_information.sql | gzip > ${workingDirectory}/${sqlDirectory}/${currentVersion}/fill_dreimt_db.sql.gz
if [ ! -z ${backendProjectPath} ]; then cp ${backendProjectPath}/additional-material/db/dreimt-db-initialization.sql ${workingDirectory}/${sqlDirectory}/${currentVersion}/dreimt-db-initialization.sql cp ${backendProjectPath}/additional-material/db/dreimt-schema.sql ${workingDirectory}/${sqlDirectory}/${currentVersion}/dreimt-schema.sql cp ${backendProjectPath}/additional-material/db/fill_full_drug_signature_interaction_table.sql ${workingDirectory}/${sqlDirectory}/${currentVersion}/fill_full_drug_signature_interaction_table.sql fi
